    def enter_number(self, digit):
        old_value = self.lcdNumber.value()
        shifted_value = old_value * 10
        self.lcdNumber.setProperty("value", shifted_value + digit)

    def plus(self):
        self._buffer1 = self.lcdNumber.value()
        self.lcdNumber.setProperty('value', 0)

